#ghub {
    grid-area: ghub;
}

#gram {
    grid-area: gram;
}

#twtr {
    grid-area: twtr;
}

sources.css/_underline.scss:
.underline {
  color: #FFBBBB;
  text-decoration: none;
  display: inline-block;
  padding: 15px 20px;
  position: relative;
  font-size: 1em;
  font-weight: bold;
}

.underline:after {
  background: none repeat scroll 0 0 transparent;
  bottom: 0;
  content: "";
  display: block;
  height: 5px;
  left: 50%;
  position: absolute;
  background: #FFBBBB;
  transition: width 0.3s ease 0s, left 0.3s ease 0s;
  width: 0;
}

.underline:hover:after {
  width: 50%;
  left: 25%;
}

.home-text>.underline {
  color: #FFBBBB;
}

.home-text>.underline:after {
  background: #FFBBBB;
}

.underline-white {
  color: white;
}

.underline-white:after {
  background: white;
}

sources.css/_project.scss:
  #notflix-project {
    background-color: white;
  }

  #notflix-project>.project {
    margin-bottom: 15%;
  }

  .project-description {
    display: inline-block;
    position: relative;
    left: 10%;
    margin: 100px 0;
    font-size: 2.5em;
    font-weight: 500;
    width: 80%;
    color: black;
  }

  .project-img-container {
    position: relative;
    margin: 100px 0;
    display: inline-block;
    width: 100%;
  }

  .image-wrapper {
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
  }

  .project-image {
    width: 80%;
  }

  .image-cover {
    width: 90%;
    height: 100%;
    background: black;
    position: absolute;
    top: 0;
    left: 0;
    align-self: center;
  }

  .scrolling-wrapper {
    margin: 100px 0;
    display: inline-block;
    overflow-x: scroll;
    overflow-y: hidden;
    white-space: nowrap;
    -webkit-overflow-scrolling: touch;
    width: 90%;
    left: 10%;
    position: relative;

    &::-webkit-scrollbar {
      display: none;
    }
  }

  /* don't display horizontal scrollbar */
  .scrolling-card {
    display: inline-block;
    margin-left: -10%;
    overflow: hidden;
    padding-left: 12%;
  }

  .scroll-cover {
    width: 100%;
    height: 98.8%;
    background: black;
    position: absolute;
    top: 0;
    left: 0;
    align-self: center;
  }

  .scrolling-img {
    max-height: 500px;
  }

  .next-project {
    width: 100%;
    height: 500px;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    overflow: hidden;
    margin-top: 100px;
    background-color: pink;
  }

  .bgWhite {
    background-color: white;
  }

  .next-project-img {
    position: absolute;
    width: 100%;
  }

  .next-project-text {
    z-index: 1;
    color: white;
    font-size: 2em;
  }

  .home-link,
  .prev-proj-link {
    width: 100%;
    height: 100px;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    overflow: hidden;
  }

  .prev-proj-link {
    background-color: #FFBBBB;
    color: white;
  }

  .home-text,
  .prev-proj-text {
    z-index: 1;
    color: pink;
    font-size: 2em;
  }

  .prev-proj-text {
    color: white;
  }

  .visit {
    color: black;
    text-decoration: underline;
  }

  .highlight {
    color: pink;
  }

  .project-title {
    color: #FFBBBB;
  left: 0%;
  position: unset;
  font-size: 2em;

}

.visit-description {
  margin: 50px 0;
}

.next-project-img-canvas {
